    'ia' => [
        1 => ['title' => 'Concepts de base IA', 'instruction' => 'Complétez la définition : L\'IA est la simulation de l\'intelligence ___ par des machines.', 'description' => 'L\'Intelligence Artificielle (IA) est un domaine de l\'informatique qui vise à créer des systèmes capables d\'effectuer des tâches nécessitant normalement l\'intelligence humaine.', 'hint' => 'L\'IA simule l\'intelligence humaine. Réponse : humaine.'],
        2 => ['title' => 'Machine Learning', 'instruction' => 'Citez les 3 types principaux de Machine Learning (Apprentissage automatique).', 'description' => 'Le Machine Learning est un sous-domaine de l\'IA. L\'apprentissage supervisé utilise des données étiquetées, l\'apprentissage non supervisé trouve des patterns sans étiquettes, et l\'apprentissage par renforcement apprend par essais et erreurs.', 'hint' => 'Le troisième type est l\'apprentissage par renforcement (reinforcement learning).'],
        3 => ['title' => 'Types d\'apprentissage', 'instruction' => 'Complétez : L\'apprentissage ___ utilise des données étiquetées pour entraîner un modèle.', 'description' => 'L\'apprentissage supervisé utilise des données étiquetées (input et output connus). L\'apprentissage non supervisé trouve des patterns sans étiquettes.', 'hint' => 'L\'apprentissage supervisé utilise des données avec des étiquettes (labels). Réponse : supervisé.'],
        4 => ['title' => 'Données et datasets', 'instruction' => 'Complétez : Un ___ est un ensemble de données utilisé pour entraîner un modèle d\'IA.', 'description' => 'Un dataset est une collection de données structurées. Le dataset d\'entraînement entraîne le modèle, le dataset de test évalue les performances.', 'hint' => 'Un dataset est un ensemble de données. Réponse : dataset.'],
        5 => ['title' => 'Algorithmes de base', 'instruction' => 'Citez 2 algorithmes de Machine Learning couramment utilisés pour la classification.', 'description' => 'Les algorithmes de classification incluent la régression logistique, les arbres de décision, les forêts aléatoires, SVM, KNN, etc.', 'hint' => 'Exemples : Régression logistique, Arbres de décision, Forêts aléatoires, SVM, KNN.'],
        6 => ['title' => 'Réseaux de neurones', 'instruction' => 'Complétez la structure d\'un réseau de neurones : Couche d\'entrée → Couches ___ → Couche de sortie.', 'description' => 'Les réseaux de neurones artificiels sont inspirés du cerveau. Ils ont une couche d\'entrée (input), des couches cachées (hidden layers), et une couche de sortie (output).', 'hint' => 'Les couches intermédiaires sont appelées "couches cachées" (hidden layers).'],
        7 => ['title' => 'Deep Learning', 'instruction' => 'Quelle bibliothèque Python open-source, développée par Google, est la plus populaire pour le Deep Learning ?', 'description' => 'TensorFlow est une bibliothèque open-source développée par Google pour le machine learning et le deep learning. Keras est une API haut niveau qui s\'appuie sur TensorFlow.', 'hint' => 'La bibliothèque s\'appelle TensorFlow. Réponse : tensorflow.'],
        8 => ['title' => 'Entraînement d\'un modèle', 'instruction' => 'Complétez : L\'___ est le processus d\'ajustement des paramètres d\'un modèle pour minimiser l\'erreur.', 'description' => 'L\'entraînement (training) ajuste les paramètres du modèle pour minimiser l\'erreur. L\'epoch est une passe complète sur le dataset.', 'hint' => 'L\'entraînement (training) ajuste les paramètres. Réponse : entraînement ou training.'],
        9 => ['title' => 'Évaluation de performance', 'instruction' => 'Citez 2 métriques utilisées pour évaluer les performances d\'un modèle de classification.', 'description' => 'Les métriques évaluent les performances. Accuracy (précision globale), Precision (précision des prédictions positives), Recall (taux de détection), F1-score (moyenne harmonique).', 'hint' => 'Métriques courantes : Accuracy, Precision, Recall, F1-score, Confusion Matrix.'],
        10 => ['title' => 'Bibliothèques Python (TensorFlow, PyTorch)', 'instruction' => 'Complétez : ___ est une bibliothèque Python open-source développée par Facebook pour le Deep Learning.', 'description' => 'TensorFlow (Google) et PyTorch (Facebook) sont les deux principales bibliothèques de Deep Learning. TensorFlow est plus mature, PyTorch est plus flexible.', 'hint' => 'PyTorch est développé par Facebook. TensorFlow est développé par Google. Réponse : PyTorch.'],
        11 => ['title' => 'Applications IA', 'instruction' => 'Citez 3 applications concrètes de l\'IA dans la vie quotidienne.', 'description' => 'L\'IA est partout : reconnaissance vocale (Siri, Alexa), voitures autonomes (Tesla), assistants virtuels, recommandations (Netflix, Amazon), reconnaissance d\'images, traduction automatique.', 'hint' => 'Exemples : reconnaissance vocale (Siri), voitures autonomes (Tesla), assistants virtuels (ChatGPT).'],
        12 => ['title' => 'Natural Language Processing', 'instruction' => 'Expliquez ce qu\'est le NLP (Natural Language Processing) et donnez 2 exemples d\'applications.', 'description' => 'Le NLP (Traitement du Langage Naturel) permet aux machines de comprendre et générer le langage humain. Applications : traduction automatique, chatbots, analyse de sentiment.', 'hint' => 'NLP = Natural Language Processing. Applications : traduction, chatbots, analyse de sentiment, etc.'],
        13 => ['title' => 'Éthique de l\'IA', 'instruction' => 'Citez 3 principes éthiques importants dans le développement de l\'IA (ex: transparence, équité, confidentialité).', 'description' => 'L\'éthique de l\'IA est cruciale. Principes clés : transparence (compréhensibilité), équité (pas de biais), confidentialité (protection des données), responsabilité.', 'hint' => 'Principes : transparence, équité, confidentialité, responsabilité, robustesse.'],
        14 => ['title' => 'Computer Vision', 'instruction' => 'Expliquez ce qu\'est la Computer Vision et donnez 2 exemples d\'applications.', 'description' => 'La Computer Vision permet aux machines de comprendre et analyser les images. Applications : reconnaissance d\'images, détection d\'objets, segmentation, classification d\'images.', 'hint' => 'Computer Vision = Vision par ordinateur. Applications : reconnaissance d\'images, détection d\'objets, voitures autonomes, etc.'],
        15 => ['title' => 'Optimisation et hyperparamètres', 'instruction' => 'Citez 3 hyperparamètres importants lors de l\'entraînement d\'un modèle de Machine Learning.', 'description' => 'Les hyperparamètres contrôlent le processus d\'entraînement. Exemples : learning rate (taux d\'apprentissage), batch size (taille du lot), nombre d\'epochs, régularisation.', 'hint' => 'Hyperparamètres courants : learning rate, batch size, nombre d\'epochs, régularisation (L1, L2), dropout rate.'],
